import React, {Component} from 'react'
import {firebaseApp} from '../components/Firebase'

export default class Test extends Component {
    constructor(props){
        super(props)
        this.itemRef = firebaseApp.database().ref('KhoaHoc')
    }
     SignUp = (props) => {

        const [userInfor, setUserInfor] = useState({
          fullName: '',
          birthday: '',
          email: '',
          studentID: '',
          phoneNumber: '',
          password: '',
          password2: ''
        })

        const handleSuccess = (values) => {
          setUserInfor({
            fullName: values.fullName, 
            birthday: values.birthday, 
            email: values.email, 
            studentID: values.studentID,         
            phoneNumber: values.phoneNumber, 
            password: values.password,
            password2: values.password2
         })

        firebaseApp.auth().createUserWithEmailAndPassword(values.email, values.password)
        .then(()=> {
          Swal.fire(
            'Chúc mừng!',
            'Bạn đã đăng ký thành công!',
            'success'
          )
        })
        .catch(function(error) {
          Swal.fire({
            icon: 'error',
            title: 'Thất bại',
            text: 'Email đã tồn tại!',
          })
        });
      }
        console.log(userInfor)
        return (
          <div>
            <Formik
            const initialValues = {{
               fullName: '',
               birthday: '',
               email: '',
               studentID: '',
               phoneNumber: '',
               password: '',
               password2: '',
            }}
            setUserInfor = {setUserInfor}
            validationSchema={validationSchema} 
            onSubmit={values=> handleSuccess(values)}>
            {({handelSubmit})=>
            <div className="signUpBody">
              <title>Đăng kí</title>
              <div className="toSignUp">
              <div className="formSignUp">
                <h2>Đăng ký học</h2>
                <img src="../img/2.png" />
            <Form className ="Formik">
                <MyInput type="text" name="fullName" label="Họ và tên"/>
                <MyInput type="date" name="birthday" label="Ngày sinh" format="dd/MM/yyyy" InputLabelProps={{shrink: true,}}/>
                <MyInput type="email" name="email" label="Email"/>
                <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
                <MyInput type="text" name="phoneNumber" label="Số điện thoại"/>
                <MyInput type="password" name="password" label="Mật khẩu"/>
                <MyInput type="password" name="password2" label="Nhập lại mật khẩu"/>
                <input id="register1" type="submit" name="SignIn" value="Đăng ký" onClick={handelSubmit}></input>
            </Form>
                <input id="register2" type="submit" name="Back" value="Trở lại" onClick={() => props.history.push('/SignIn')}></input>
                {/* <input id="register2" type="submit" name="Back" value="Trở lại" onClick={setData(itemRef)}></input>
                <input id="register2" type="submit" name="Back" value="Trở lại" onClick={pushData(itemRef)}></input> */}
            </div>
            </div>
            </div>
            }
            </Formik>
          </div>  
            );
          }
    render() {
        SignUp(this.props)
    }
}
