import React, { useState } from 'react'
import {Formik, Form} from 'formik'
import { MyInput } from './MyInput'
import '../css/SignIn.css'
import {firebaseApp} from '../components/Firebase'
import Swal from 'sweetalert2'
import * as yup from 'yup'


const validationSchema = yup.object({
    studentID:yup.string()
    .required("Vui lòng nhập mã số SV"),

    password:yup.string()
    .required("Vui lòng nhập mật khẩu"),
})

const SignIn = (props) => {
    const [userInfor, setUserInfor] = useState({
      email:'',
      password:'',
    })

const handleSuccess = (values) => {
  setUserInfor({
    email: values.email, 
    password: values.password,
  })
  firebaseApp.auth().signInWithEmailAndPassword(values.email, values.password).then(()=> {

})
  Swal.fire(
    'Chúc mừng!',
    'Bạn đã đăng ký thành công!',
    'success'
  )
}
  return (
    <div>
      <Formik
      const initialValues = {{
        email: '',
        password: '',
      }}
      validationSchema={validationSchema} 
      onSubmit={values=> handleSuccess(values)}>
      {({handelSubmit})=>
      <div className="signInBody">
        <title>Đăng kí</title>
        <div className="toSignIn">
        <div className="formSignIn">
          <h2>Đăng nhập</h2>
          <img src="../img/2.png" />
      <Form className ="Formik">
          <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
          <MyInput type="password" name="password" label="Mật khẩu"/>
          <input id="signIn1" type="submit" name="SignIn" value="Đăng nhập" onClick={handelSubmit}></input>
      </Form>
          <input id="signIn2" type="submit" name="Back" value="Đăng ký" onClick={() => props.history.push('/SignUp')}></input>
          {/* <input id="signIn2" type="submit" name="Back" value="Đăng ký" onClick={() => setCount(count +1)}></input> */}
      </div>
      </div>
      </div>
      }
      </Formik>
    </div>  
         );
    }

export default SignIn
