import React, { useState } from 'react'
import {Formik, Form} from 'formik'
import { MyInput } from './MyInput'
import '../css/SignUp.css'
import {firebaseApp} from '../components/Firebase'
import Swal from 'sweetalert2'
import * as yup from 'yup'

const validationSchema = yup.object({
  fullName:yup.string()
  .required("Vui lòng nhập đầy đủ họ tên")
  .matches(/^([^0-9]*)$/,"Tên chỉ bao gồm ký tự từ a-z")
  .max(30,"Tên quá dài")
  .min(5,"Tên quá ngắn"),

  birthday:yup.string()
  .required("Vui lòng nhập ngày sinh"),

  email:yup.string()
  .email('Email không hợp lệ')
  .required("Vui lòng nhập email"),

  studentID:yup.string()
  .required('Vui lòng nhập mã số sinh viên'),

  phoneNumber:yup.string()
  .required('Vui lòng nhập mã số điện thoại')
  .matches(/^([^a-z]*)$/,"Số điện thoại chỉ gồm 10 số từ 0-9")
  .max(10,"Số điện thoại bao gồm 10 số")
  .min(10,"Số điện thoại bao gồm 10 số"),

  password:yup.string()
  .min(6,"Mật khẩu quá ngắn")
  .required('Vui lòng nhập mật khẩu'),

  password2:yup.string()
  .oneOf([yup.ref('password')], 'Mật khẩu không giống mật khẩu đã nhập')
  .required('Vui lòng nhập lại mật khẩu'),
})

const handleBlur = (values) =>{
  var firstEmail = values.email;
  var emailID = firstEmail.slice(0, firstEmail.indexOf("."));
  firebaseApp.database().ref('userInform/' + emailID).child('email').on('value', function(snapshot){
    emailID = snapshot.val()
  })
  if (values.email == emailID) {
    Swal.fire({
      icon: 'error',
      title: 'Thất bại',
      text: 'Email đã tồn tại!',
    })
  }
  console.log(emailID)
  return emailID
}

const SignUp = (props) => {

  const [userInfor, setUserInfor] = useState({
    fullName: '',
    birthday: '',
    email: '',
    studentID: '',
    phoneNumber: '',
    password: '',
    password2: ''
  })

  const handleSuccess = (values) => {
    setUserInfor({
      fullName: values.fullName, 
      birthday: values.birthday, 
      email: values.email, 
      studentID: values.studentID,         
      phoneNumber: values.phoneNumber, 
      password: values.password,
      password2: values.password2
   })

  firebaseApp.auth().createUserWithEmailAndPassword(values.email, values.password)
  .then(()=> {
    Swal.fire(
      'Chúc mừng!',
      'Bạn đã đăng ký thành công!',
      'success'
    )
    .then((result) => {
      var firstEmail = values.email;
      var emailID = firstEmail.slice(0, firstEmail.indexOf("."))
      firebaseApp.database().ref('userInform/' + emailID).set ({
        fullName : values.fullName,
        birthday: values.birthday, 
        email: values.email, 
        studentID: values.studentID,         
        phoneNumber: values.phoneNumber, 
        password: values.password
    })
      if(result.value){
        props.history.push('/SignIn')
      }
    });
  })
  .catch(function(error) {
    Swal.fire({
      icon: 'error',
      title: 'Thất bại',
      text: 'Email đã tồn tại!',
    })
  });
}
  console.log(userInfor)
  return (
    <div>
      <Formik
      const initialValues = {{
         fullName: '',
         birthday: '',
         email: '',
         studentID: '',
         phoneNumber: '',
         password: '',
         password2: '',
      }}
      setUserInfor = {setUserInfor}
      validationSchema={validationSchema} 
      onBlur={values => handleBlur(values)}
      onSubmit={values=> handleSuccess(values)}>
      {({handelSubmit})=>
      <div className="signUpBody">
        <title>Đăng kí</title>
        <div className="toSignUp">
        <div className="formSignUp">
          <h2>Đăng ký học</h2>
          <img src="../img/2.png"/>
      <Form className ="Formik">
          <MyInput type="text" name="fullName" label="Họ và tên"/>
          <MyInput type="date" name="birthday" label="Ngày sinh" format="dd/MM/yyyy" InputLabelProps={{shrink: true,}}/>
          <MyInput id ='email' type="email" name="email" label="Email"/>
          <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
          <MyInput type="text" name="phoneNumber" label="Số điện thoại"/>
          <MyInput type="password" name="password" label="Mật khẩu"/>
          <MyInput type="password" name="password2" label="Nhập lại mật khẩu"/>
          <button id="register1" type="submit" name="SignIn" onClick={handelSubmit}>Đăng ký</button>
      </Form>
          {/* <input id="register2" type="submit" name="Back" value="Trở lại" onClick={() => props.history.push('/SignIn')}/> */}
          {/* {/* <input id="register2" type="submit" name="Back" value="Trở lại" onClick={setData(itemRef)}></input> */}
          <button id="register2" name="Back" value="Trở lại" onClick={() => props.history.push('/SignIn')}>Trở lại</button> 
      </div>
      </div>
      </div>
      }
      </Formik>
    </div>  
      );
    }

export default SignUp
