import React from 'react'
import {Formik, Form} from 'formik'
import { MyInput } from './MyInput'
import '../css/SignUp.css'
import * as yup from 'yup'


const validationSchema = yup.object({
  fullName:yup.string()
  .required("Vui lòng nhập đầy đủ họ tên")
  .matches(/^([^0-9]*)$/,"Tên chỉ bao gồm ký tự từ a-z")
  .max(30,"Tên quá dài")
  .min(5,"Tên quá ngắn"),

  birthday:yup.string()
  .required("Vui lòng nhập ngày sinh"),

  email:yup.string()
  .email('Email không hợp lệ')
  .required("Vui lòng nhập email"),

  studentID:yup.string()
  .required('Vui lòng nhập mã số sinh viên'),

  phoneNumber:yup.string()
  .required('Vui lòng nhập mã số điện thoại')
  .matches(/^([^a-z]*)$/,"số điện thoại chỉ gồm số từ 0-9")
  .max(10,"số điện thoại bao gồm 10 số")
  .min(10,"số điện thoại bao gồm 10 số")
})

const handleChange = prop => event => {
  setValues({ ...values, [prop]: event.target.value });
};

const handleClickShowPassword = () => {
  setValues({ ...values, showPassword: !values.showPassword });
};

const handleMouseDownPassword = event => {
  event.preventDefault();
};

const SignUp = () => {
  return (
    <div>
      <Formik
      initialValues = {{
         fullName: '',
         birthday: '',
         email: '',
         studentID: '',
         phoneNumber: ''
      }}
      validationSchema={validationSchema} 
      onSubmit={values=>console.log(values)}>
      {({handelSubmit})=>
      <div className="SignUpBody">
        <title>Đăng kí</title>
        <div className="toSignUp">
        <div className="formSignUp">
          <h2>Đăng ký học</h2>
          <img src="../img/2.png" />
      <Form className ="Formik">
          <MyInput type="text" name="fullName" label="Họ và tên"/>
          <MyInput type="date" name="birthday" label="Ngày sinh" format="dd/MM/yyyy" InputLabelProps={{shrink: true,}}/>
          <MyInput type="email" name="email" label="Email"/>
          <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
          <MyInput type="text" name="phoneNumber" label="Số điện thoại"/>
          <Input
            id="standard-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
          />
          <input id="register" type="submit" name="Đăng ký" value="Đăng ký" onClick={handelSubmit}></input>
      </Form>
      </div>
      </div>
      </div>
      }
      </Formik>
    </div>

          // <form>
          //   <label>Họ và tên</label>
          //   <input type="text" name="fullName"/>
          //   <label>Ngày Sinh</label>
          //   <input type="date" name="birthday" format="dd/mm/yyyy"/>
          //   <label>Mã số sinh viên</label>
          //   <input type="text" name="studentID"/>
          //   <label>Số điện thoại</label>
          //   <input type="text" name="phone"/>
          //   <label>Mật khẩu</label>
          //   <input type="password" name="password"/>
          //   <label>Nhập lại mật khẩu</label>
          //   <input type="password" name="password2"/>
          //   <label>Email</label>
          //   <input type="text" name="email" />
          //   <input id="register" type="submit" name="Đăng ký" value="Đăng ký"/>
          // </form>
          //   <input id="register" type="submit" name="Quay lại" value="Quay lại"/>
         );
    }

export default SignUp
