import React, {Component} from 'react'
import {Formik, Form} from 'formik'
import { MyInput } from './MyInput'
import { withRouter } from 'react-router-dom';
import '../css/SignIn.css'
import * as yup from 'yup'

class SignIn extends Component {
    constructor(props) {
        super(props);
    }

    handleSignUp = () => {
        this.props.history.push("/SignUp")
    }
    validationSchema = yup.object({
        studentID:yup.string()
        .required("Vui lòng nhập mã số SV"),

        password:yup.string()
        .required("Vui lòng nhập mật khẩu"),
    })


    onSignIn = () => {
      return (
        <div>
          <Formik
          const initialValues = {{
             studentID: '',
             password: '',
          }}
          handleSignUp={handleSignUp}
          validationSchema={validationSchema} 
          onSubmit={values=>console.log(values)}>
          {({handelSubmit})=>
          <div className="signInBody">
            <title>Đăng kí</title>
            <div className="toSignIn">
            <div className="formSignIn">
              <h2>Đăng nhập</h2>
              <img src="../img/2.png" />
          <Form className ="Formik">
              <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
              <MyInput type="password" name="password" label="Mật khẩu"/>
              <input id="signIn1" type="submit" name="SignIn" value="Đăng nhập" onClick={handelSubmit}></input>
          </Form>
              <input id="signIn2" type="submit" name="Back" value="Đăng ký" onClick={handleSignUp}></input>
          </div>
          </div>
          </div>
          }
          </Formik>
        </div>  
             );
        }

    render() {
        return (
            onSignIn()
        )
    }
}



export default withRouter(SignIn)
