import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import '../css/AppHeader.css'
import {
  Collapse,
  Navbar,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  ButtonToggle
} from 'reactstrap';
import { useSelector } from 'react-redux';

const AppHeader = (props) => {
    const {userInform} = useSelector(state => state.userReducer)

    // let headerItem 
    // if (localStorage.getItem('login') === null){
    //   headerItem = [
    //     <ButtonToggle color="primary" onClick={()=> this.props.history.push('/SignIn')}>Đăng nhập</ButtonToggle>
    //   ]
    // }
    // else {
    //   headerItem = [
        // <DropdownToggle >
        //   <img src="../img/logo.png" height="30px"></img>
        // </DropdownToggle>,
        // <DropdownMenu right>
        //   <DropdownItem >
        //     <NavLink href="/Profile">Thông tin cá nhân</NavLink>
        //   </DropdownItem>
        //   <DropdownItem divider/>
        //   <DropdownItem>
        //       <NavLink href="/Home" onClick={() => {localStorage.removeItem('login')}}>Đăng xuất</NavLink>
        //   </DropdownItem>
        // </DropdownMenu>
    //   ]
    //}

  return (
    <div>
      <Navbar color="light" light expand="md" className="Header">
        <NavbarBrand href="/Home">Code C</NavbarBrand>
          <Collapse navbar>
            <Nav className="mr-auto" navbar>
              <NavItem>
                <NavLink href="/components/">Hướng dẫn</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
            </Nav>
            <UncontrolledDropdown inNavbar className="Header-Profile">
              {Object.keys(userInform).length !== 0 ? (
              <DropdownToggle>
                <img src="../img/logo.png" height="30px"/>
              </DropdownToggle>,
              <DropdownMenu right>
              <DropdownItem >
              <NavLink href="/Profile">Thông tin cá nhân</NavLink>
              </DropdownItem>
              <DropdownItem divider/>
          <DropdownItem>
              <NavLink href="/Home" onClick={() => {localStorage.removeItem('login')}}>Đăng xuất</NavLink>
          </DropdownItem>
        </DropdownMenu>) : (<DropdownToggle>
                <img src="../img/logo.png" height="30px"/>
              </DropdownToggle>,
              <DropdownMenu right>
              <DropdownItem >
              <NavLink href="/Profile">Thông tin cá nhân</NavLink>
              </DropdownItem>
              <DropdownItem divider/>
          <DropdownItem>
              <NavLink href="/Home" onClick={() => {localStorage.removeItem('login')}}>Đăng xuất</NavLink>
          </DropdownItem>
        </DropdownMenu>)}
            </UncontrolledDropdown>
          </Collapse>
      </Navbar>
    </div>
    );
}

export default withRouter(AppHeader);
