import React from 'react'
import {Formik, Form} from 'formik'
import { MyInput } from './MyInput'
import '../css/SignUp.css'
import * as yup from 'yup'


const validationSchema = yup.object({
  fullName:yup.string()
  .required("Vui lòng nhập đầy đủ họ tên")
  .matches(/^([^0-9]*)$/,"Tên chỉ bao gồm ký tự từ a-z")
  .max(30,"Tên quá dài")
  .min(5,"Tên quá ngắn"),

  birthday:yup.string()
  .required("Vui lòng nhập ngày sinh"),

  email:yup.string()
  .email('Email không hợp lệ')
  .required("Vui lòng nhập email"),

  studentID:yup.string()
  .required('Vui lòng nhập mã số sinh viên'),

  phoneNumber:yup.string()
  .required('Vui lòng nhập mã số điện thoại')
  .matches(/^([^a-z]*)$/,"số điện thoại chỉ gồm 10 số từ 0-9")
  .max(10,"số điện thoại bao gồm 10 số")
  .min(10,"số điện thoại bao gồm 10 số"),

  password:yup.string()
  .required('Vui lòng nhập mật khẩu'),

  password2:yup.string()
  .required('Vui lòng nhập lại mật khẩu'),
})

const SignUp = () => {
  return (
    <div>
      <Formik
      initialValues = {{
         fullName: '',
         birthday: '',
         email: '',
         studentID: '',
         phoneNumber: '',
         password: '',
         password2: '',
      }}
      validationSchema={validationSchema} 
      onSubmit={values=>console.log(values)}>
      {({handelSubmit})=>
      <div className="SignUpBody">
        <title>Đăng kí</title>
        <div className="toSignUp">
        <div className="formSignUp">
          <h2>Đăng ký học</h2>
          <img src="../img/2.png" />
      <Form className ="Formik">
          <MyInput type="text" name="fullName" label="Họ và tên"/>
          <MyInput  format="dd/MM/yyyy" type="date" name="birthday" label="Ngày sinh"  InputLabelProps={{shrink: true,}}/>
          <MyInput type="email" name="email" label="Email"/>
          <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
          <MyInput type="text" name="phoneNumber" label="Số điện thoại"/>
          <MyInput type="password" name="password" label="Mật khẩu"/>
          <MyInput type="password" name="password2" label="Nhập lại mật khẩu"/>
          <input id="register" type="submit" name="signUp" value="Đăng ký" onClick={handelSubmit}></input>
      </Form>
          <input id="register" type="submit" name="back" value="Trở lại" onClick={handelSubmit}></input>
      </div>
      </div>
      </div>
      }
      </Formik>
    </div>

          // <form>
          //   <label>Họ và tên</label>
          //   <input type="text" name="fullName"/>
          //   <label>Ngày Sinh</label>
          //   <input type="date" name="birthday" format="dd/mm/yyyy"/>
          //   <label>Mã số sinh viên</label>
          //   <input type="text" name="studentID"/>
          //   <label>Số điện thoại</label>
          //   <input type="text" name="phone"/>
          //   <label>Mật khẩu</label>
          //   <input type="password" name="password"/>
          //   <label>Nhập lại mật khẩu</label>
          //   <input type="password" name="password2"/>
          //   <label>Email</label>
          //   <input type="text" name="email" />
          //   <input id="register" type="submit" name="Đăng ký" value="Đăng ký"/>
          // </form>
          //   <input id="register" type="submit" name="Quay lại" value="Quay lại"/>
         );
    }

export default SignUp
