import React from 'react'
import {Formik, Form} from 'formik'
import { MyInput } from './MyInput'
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import '../css/SignUp.css'
import * as yup from 'yup'

const validationSchema = yup.object({
  fullName:yup.string()
  .required("Vui lòng nhập đầy đủ họ tên")
  .matches(/^([^0-9]*)$/,"Tên chỉ bao gồm ký tự từ a-z")
  .max(30,"Tên quá dài")
  .min(5,"Tên quá ngắn"),

  birthday:yup.string()
  .required("Vui lòng nhập ngày sinh"),

  email:yup.string()
  .email('Email không hợp lệ')
  .required("Vui lòng nhập email"),

  studentID:yup.string()
  .required('Vui lòng nhập mã số sinh viên')
  .matches(/^([^a-z]*)$/,"Tên chỉ bao gồm ký tự từ a-z")
})

  const handleDateChange = date => {
    setSelectedDate(date);
  };

const SignUp = () => {
  return (
    <div>
      <Formik
      initialValues = {{
         fullName: '',
         birthday: '',
         email: '',
         studentID: ''
      }}
      validationSchema={validationSchema} 
      onSubmit={values=>console.log(values)}>
      {({handelSubmit})=>
      <div className="SignUpBody">
        <title>Đăng kí</title>
        <div className="toSignUp">
        <div className="formSignUp">
          <h2>Đăng ký học</h2>
          <img src="../img/2.png" />
      <Form className ="Formik">
          <MyInput type="text" name="fullName" label="Họ và tên"/>
          <MyInput type="date" name="birthday" label="Ngày sinh" format="dd/MM/yyyy" InputLabelProps={{shrink: true,}}/>

          <MyInput type="email" name="email" label="Email"/>
          <MyInput type="text" name="studentID" label="Mã số sinh viên"/>
          <input id="register" type="submit" name="Đăng ký" value="Đăng ký" onClick={handelSubmit}></input>
      </Form>
      </div>
      </div>
      </div>
      }
      </Formik>
    </div>

          // <form>
