import React from 'react'
import * as yup from "yup";
import { Formik, Form } from "formik";
import { MyInput } from "./MyInput";
import { ButtonToggle } from "reactstrap";
import { useDispatch } from 'react-redux';
import { changePassword } from '../redux/action/userAction';

const ProfileChangePassword = (props) => {

    const dispatch = useDispatch()

    const validationPassword = yup.object({
        password:yup.string()
        .min(6,"Mật khẩu quá ngắn")
        .required('Vui lòng nhập mật khẩu'),

        password2:yup.string()
        .oneOf([yup.ref('password')], 'Mật khẩu không giống mật khẩu đã nhập')
        .required('Vui lòng nhập lại mật khẩu'),
      })

    return (
        <div className="Profile">
            <div id="rightProfile">
            <Formik
            const initialValues = {
              {
                  password: '',
                  password2: ''
              }
          }
          validationSchema = { validationPassword }
          onSubmit = {values => dispatch(changePassword(values))}>  
          {({ handelSubmit }) =>
            <div>
              <Form className = "formikProfile">
                <p>Mật khẩu mới:</p>
                <MyInput id="password" type = "text" name= "password" variant="outlined"/>
                <p>Xác nhận mật khẩu:</p>
                <MyInput id="password2" type = "text" name= "password2" variant="outlined"/>
                <ButtonToggle id="accept" color="primary" onClick={handelSubmit}>Xác nhận</ButtonToggle>
              </Form> 
            </div>                 
          } 
        </Formik>
            </div>
        </div>
    )
}

export default ProfileChangePassword
