import React, {Component} from 'react'
import {firebaseApp} from '../components/Firebase'

export default class RealtimeDB extends Component {
    constructor(props){
        super(props);
        this.itemRef = firebaseApp.database().ref('UserInform');
    }
    setDB() {
        this.itemRef.set({
            ReactNative : "KN"
        })
    }
    render () {
        return(
        <div>
            <button onClick={setDB()}></button>
        </div>
        )
    };
}
